document.addEventListener("DOMContentLoaded",function(){const l=document.querySelectorAll(".navbar-burger"),t=document.querySelectorAll(".navbar-menu");if(l.length&&t.length)for(var e=0;e<l.length;e++)l[e].addEventListener("click",function(){for(var a=0;a<t.length;a++)t[a].classList.toggle("hidden")});const o=document.querySelectorAll(".navbar-close"),r=document.querySelectorAll(".navbar-backdrop"),c=document.querySelectorAll("a:not(.mobile-menu-icon)");if(o.length)for(var e=0;e<o.length;e++)o[e].addEventListener("click",function(){for(var n=0;n<t.length;n++)t[n].classList.toggle("hidden")});if(r.length)for(var e=0;e<r.length;e++)r[e].addEventListener("click",function(){for(var n=0;n<t.length;n++)t[n].classList.toggle("hidden")});if(c.length)for(var e=0;e<c.length;e++)c[e].addEventListener("click",function(){for(var n=0;n<t.length;n++)t[n].classList.add("hidden")})});
